        if (op->call_op.num_vars < 1 || op->call_op.num_vars > 2) {
            eshkol_warn("write-line requires 1 or 2 arguments");
            return nullptr;
        }

        Function* fputs_func = function_table["fputs"];
        Function* fputc_func = function_table["fputc"];
