        Value* bg_k = builder->CreateLoad(Type::getInt64Ty(*context), bg_copy_idx);
        Value* bg_k_less_n = builder->CreateICmpULT(bg_k, n);
        builder->CreateCondBr(bg_k_less_n, bg_copy_body, bg_copy_exit);
        
